import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components/native";
import Constants from "expo-constants";
import Icon from "react-native-vector-icons/Ionicons";
import ComentariosContainer from "./comentarioContainer";
import { ComentarioProps } from "../../utils/interfaces";
import { getCurrentDate } from "../../utils/time";
import { Comentario } from "../../model/Comentario";
import { Usuario } from "../../model/Usuario";
import { Nav } from "./Nav";
import SockJS from "sockjs-client";
import Stomp, { Client } from "stompjs";

const TodosComentarios: React.FC<ComentarioProps> = ({ route }) => {
    const { publicacao } = route.params;
    const [message, setMessage] = useState<string>('');
    const [comentarios, setComentarios] = useState<Comentario[]>([]);
    const [loading, setLoading] = useState<boolean>(true);

    const webSock = useRef<Client | null>(null);

    useEffect(() => {
        var sock = new SockJS("http://10.0.0.181:8080/ws");
        let stompClient: Client = Stomp.over(sock);
        webSock.current = stompClient;

        webSock.current.connect({}, function (frame) {
            console.log("Conectado ao WebSocket!");
        });

        return () => {
            if (webSock.current) {
                webSock.current.disconnect(() => {console.log("Desconectado.")});
            }
        };
    }, []);

    const enviar = () => {
        if (webSock.current != null && webSock.current.connected) {
            const messageJSON = {
                comentario: message,
                codigoAcesso: 12345,
                idPublicacao: 1,
                idUsuario: 1
            };
            webSock.current?.send("comentarioWSSend", {}, JSON.stringify(messageJSON));

            const usuario = Usuario.builder()
                .withApelido("LeviJunior")
                .withId(1)
                .build();

            const novoComentario = Comentario.builder()
                .withComentario(message)
                .withId(comentarios.length + 1)
                .withUsuario(usuario)
                .build();

            setComentarios((prevComentarios) => [...prevComentarios, novoComentario]);

            setMessage("");

            console.log("Enviado");
        } else {
            console.error("Erro: WebSocket não conectado");
        }
    };

    return (
        <Container>
            <Nav></Nav>
            <ContainerPublicacao>
                <ContainerUsuario>
                    <NomeUsuario>@{publicacao.getUsuario().getApelido()}</NomeUsuario>
                    <TempoPublicacao>{getCurrentDate(publicacao.getDate())}</TempoPublicacao>  
                </ContainerUsuario>
                <Publicacao>{publicacao.getPublicacao()}</Publicacao>
            </ContainerPublicacao>
            <ComentarioContainer>
                <ComentariosContainer 
                loading={loading}
                id={publicacao.getId()} 
                comentarios={comentarios}
                setComentarios={setComentarios}
                />
                <EscreverComentario>
                    <Input 
                    placeholder="Escreva uma mensagem..." 
                    placeholderTextColor={"white"} 
                    onChangeText={(text) => setMessage(text)}
                    value={message}
                    cursorColor={"white"} 
                    multiline={true}></Input>
                    <BotaoEnviar onPress={() => enviar()}>
                        <Icon name={"send"} color={"green"} size={24}/>
                    </BotaoEnviar>
                </EscreverComentario>
            </ComentarioContainer>
        </Container>
    )
}

export default TodosComentarios;

const Container = styled.SafeAreaView`
    flex: 1;
    margin-top: ${Constants.statusBarHeight}px;
    background-color: #303030;
`

const ContainerPublicacao = styled.View`
    min-height: 140px;
    width: 100%;
    background-color: #136075;
    padding-horizontal: 10px;
    padding-vertical: 20px;
`

const NomeUsuario = styled.Text`
    font-size: 16px;
    color: white;
    font-weight: 500;
`

const TempoPublicacao = styled.Text`
    font-size: 11px;
    color: white;
`
const ContainerUsuario = styled.View`
    width: 70%;
    height: 60px;
    flex-direction: column;
`

const Publicacao = styled.Text`
    width: 100%;
    min-height: 10px;
    color: white;
`

const ComentarioContainer = styled.View`
    flex: 1;
    background-color: #303030;
`
const EscreverComentario = styled.View`
    position: absolute;
    z-index: 2;
    background-color: #303030;
    width: 100%;
    min-height: 50px;
    max-height: 130px;
    bottom: 0px;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    border-top-width: 1px;
    border-top-color: white;
`

const Input = styled.TextInput`
    width: 90%;
    min-height: 46px;
    max-height: 126px;
    color: white;
    font-size: 16px;
    padding-horizontal: 2px;
    padding-vertical: 6px;
`

const BotaoEnviar = styled.TouchableOpacity`
    justify-content: center;
    align-items: center;
    height: 46px;
    width: 10%;
`
