import { StatusBar } from 'expo-status-bar';
import { FlatList } from 'react-native';
import styled from 'styled-components/native';
import Constants from 'expo-constants';
import { useEffect, useState } from 'react';
import { Usuario } from '../../model/Usuario';
import { Comentario } from '../../model/Comentario';
import { Publicacao } from '../../model/Publicacao';
import { PublicacaoUser } from '../publicacao/publicacao';
import { getPublicacoes } from '../publicacao/getPublicacoes';

const usuario: Usuario = Usuario.builder()
  .withId(1)
  .withApelido("Levi")
  .build();

const comentario: Comentario = Comentario.builder()
  .withId(1)
  .withComentario("Boa tarde!")
  .withUsuario(usuario)
  .build();

const publicacao: Publicacao = Publicacao.builder()
  .withId(1)
  .withPublicacao("Bom dia!")
  .withDate(new Date())
  .withUsuario(usuario)
  .withComentarios([comentario])
  .build();

export default function App() {
  const [publicacoes, setPublicacoes] = useState<Publicacao[]>([publicacao]);

  useEffect(() => {
    getPublicacoes(setPublicacoes);
  },[])

  return (
    <Container>
      <StatusBar backgroundColor="#303030"></StatusBar>
      <FlatList
      ItemSeparatorComponent={() => <Separator/>}
      data={publicacoes}
      renderItem={({index, item}) => 
        <PublicacaoUser publicacao={item} index={index}></PublicacaoUser>
      }/>
    </Container>
  );
}

const Container = styled.View`
  width: 100%;
  height: 100%;
  background-color: #303030;
  margin-top: ${Constants.statusBarHeight};
`

const Separator = styled.View`
  height: 4px;
`
