import { Dispatch, SetStateAction } from "react";
import { Publicacao } from "../../model/Publicacao";

export const getPublicacoes = async(setPublicacoes: Dispatch<SetStateAction<Publicacao[]>>) => {
    try {
        const response = await fetch('http://localhost:8080/v1/publicacoes');

        if (response.ok) {
            const data = await response.json();
            const publicacoes: Publicacao[] = data.map((item: any) => {
              const date = new Date(item.date);

              return Publicacao.builder()
                .withId(item.id)
                .withUsuario(item.usuario)
                .withDate(date)
                .withComentarios(item.comentarios)
                .withPublicacao(item.publicacao)
                .build();
            });

            setPublicacoes(publicacoes);
        } else {
            console.error('Erro na requisição HTTP');
        }
        } catch (error) {
            console.error('Erro ao buscar os dados:', error);
    }
}
